import { Listener, SpringSystem, SpringConfig } from 'rebound';
export default class MultiSpring<Numbers extends number[]> {
    private system;
    private config;
    private springs;
    constructor(system: SpringSystem, config: SpringConfig);
    destroy(): void;
    getCurrentValue(): Numbers;
    setCurrentValue(value: Numbers, skipSetAtRest?: boolean): void;
    getEndValue(): number[];
    setEndValue(value: Numbers): void;
    getVelocity(): number[];
    setAtRest(): void;
    setVelocity(value: number): void;
    setRestSpeedThreshold(value: number): void;
    setRestDisplacementThreshold(value: number): void;
    setOvershootClampingEnabled(value: boolean): void;
    addListener(value: Listener): void;
    removeListener(value: Listener): void;
    removeAllListeners(): void;
}
